sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
		this.rightNow = new GlideDateTime();
	},

	isDatePast: function(strDate) {
		// Create GlideDateTime objects for the passed in date
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(this.rightNow)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate) {
		// Create a GlideDateTime object for the When needed date passed in from caller
		var whenNeeded = new GlideDateTime(strDate);

		// Get date portion of vars
		var today = this.rightNow.getLocalDate();
		var isToday = whenNeeded.getLocalDate();

		// If the When needed date is today, return false, else return true
		if (today.compareTo(isToday) == 0) {
			return false;
		} else {
			return true;
		}
	},

	type: 'NeedItUtils'
};
