sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();
var twentyFour = new GlideDateTime();
twentyFour.addDaysLocalTime(1);

// Query the database for NeedIt Task records with Due date field values due
// between the current time and 24 hours hence.  Only return NeedIt Task records
// that do not have a State field value of Closed Complete.
var nITasksDueSoon = new GlideRecord('x_58872_needit_needit_task');
nITasksDueSoon.addQuery('due_date', '>', rightNow);
nITasksDueSoon.addQuery('due_date', '<', twentyFour);
nITasksDueSoon.addQuery('state','!=',3);
nITasksDueSoon.query();

// Write a log message for each overdue NeedIt Task Record
while(nITasksDueSoon.next()){
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', nITasksDueSoon, nITasksDueSoon.number, gs.getUserName(), 'needit');
	gs.info("NeedIt task record due soon = " + nITasksDueSoon.number);
}
